(function() {
    'use strict';

    angular.module('app')
        .factory('BreadCrumbService', BreadCrumbService);

    BreadCrumbService.$inject = [];

    /* @ngInject */
    function BreadCrumbService() {

        var service = {
            crumbs: [],
            set: set,
            getCrumbs: getCrumbs
        }

        return service;

        //////// SERIVCE METHODS ////////

        function getCrumbs() {
            return service.crumbs;
        }

        function set(str) {
            var res = str.split('.');
            var state = '';
            service.crumbs = [];
            angular.forEach(res, function(val, index) {
                if (index == 0) {
                    state = val;
                } else {
                    state += '.' + val;
                }

                var obj = { name: ucFirst(val), state: state };
                service.crumbs.push(obj);
            });
        }

        function ucFirst(string) {
            return string.charAt(0).toUpperCase() + string.slice(1);
        }
    }

})();
(function() {
    'use strict';

    angular.module('app')
        .service('SmoothScroll', SmoothScroll);

    function SmoothScroll() {

        this.scrollTo = function(eID) {

            // This scrolling function 
            // is from http://www.itnewb.com/tutorial/Creating-the-Smooth-Scroll-Effect-with-JavaScript

            var startY = currentYPosition();
            var stopY = elmYPosition(eID);
            var distance = stopY > startY ? stopY - startY : startY - stopY;
            if (distance < 100) {
                scrollTo(0, stopY);
                return;
            }
            var speed = Math.round(distance / 100);
            if (speed >= 20) speed = 20;
            var step = Math.round(distance / 25);
            var leapY = stopY > startY ? startY + step : startY - step;
            var timer = 0;
            if (stopY > startY) {
                for (var i = startY; i < stopY; i += step) {
                    setTimeout("window.scrollTo(0, " + leapY + ")", timer * speed);
                    leapY += step;
                    if (leapY > stopY) leapY = stopY;
                    timer++;
                }
                return;
            }
            for (var i = startY; i > stopY; i -= step) {
                setTimeout("window.scrollTo(0, " + leapY + ")", timer * speed);
                leapY -= step;
                if (leapY < stopY) leapY = stopY;
                timer++;
            }

            function currentYPosition() {
                // Firefox, Chrome, Opera, Safari
                if (self.pageYOffset) return self.pageYOffset;
                // Internet Explorer 6 - standards mode
                if (document.documentElement && document.documentElement.scrollTop)
                    return document.documentElement.scrollTop;
                // Internet Explorer 6, 7 and 8
                if (document.body.scrollTop) return document.body.scrollTop;
                return 0;
            }

            function elmYPosition(eID) {
                var elm = document.getElementById(eID);
                var y = elm.offsetTop;
                var node = elm;
                while (node.offsetParent && node.offsetParent != document.body) {
                    node = node.offsetParent;
                    y += node.offsetTop;
                }
                return y;
            }

        };
    }

})();
(function() {
    'use strict';

    angular.module('app.auth', [])
        .factory('AuthService', AuthService);

    AuthService.$inject = ['$auth', '$rootScope', '$q', '$state', 'CONST', '$log'];

    /* @ngInject */
    function AuthService($auth, $rootScope, $q, $state, CONST, $log) {
        var api = CONST.api_domain;

        var service = {
            login: login,
            logout: logout,
            userIsAuthenticated: userIsAuthenticated,
            currentUser: currentUser,
            tokenExists: tokenExists,
            invalidateTokens: invalidateTokens,
            requestReset: requestReset
        }

        return service;

        ////////////////

        function userIsAuthenticated() {
            return $auth.userIsAuthenticated();
        }

        function currentUser() {
            return $auth.user;
        }

        function tokenExists() {
            return (!$auth.retrieveData('auth_headers') ? false : true);
        }

        function invalidateTokens() {
            $auth.invalidateTokens();
        }

        function login(credentials) {
            var d = $q.defer();

            $auth.submitLogin(credentials).then(function(resp) {
                d.resolve(resp);
            }).catch(function(err) {
                $log.log(err);
                d.reject(err);
            });

            return d.promise;
        }

        function requestReset(params) {
            var d = $q.defer();

            $auth.requestPasswordReset(params).then(function(resp) {
                d.resolve(resp);
            }).catch(function(err) {
                $log.log(err);
                d.reject(err);
            });

            return d.promise;
        }

        function logout() {
            var d = $q.defer();

            $auth.signOut().then(function(resp) {
                d.resolve(resp);
            }).catch(function(err) {
                $log.log(err);
                d.reject(err);
            });

            return d.promise;
        }

    }

})();

